import MainContent from "./MainContent";
import Navbar from "./Navbar";

interface LayoutProps {
    header: boolean;
    children?: React.ReactNode;
}

const Layout = ({ header = true, children }: LayoutProps) => {
  return (
    <>
      {header && <Navbar />}
      <MainContent>
        {children}
      </MainContent>
    </>
  );
};

export default Layout;
